import React, {useEffect, useState, useRef} from 'react'
import dynamic from 'next/dynamic'
import { select } from "d3"

const Page = (props) => {
	const [windowsWidth, setWidth] = useState()
	const [windowsHeight, setHeight] = useState()
	const [data, setData] = useState(null);
	const svgRef = useRef();
	const title = props.props.map(e => e.title);
	if( window !== undefined){
			setWidth(window.innerWidth);
			setHeight(window.innerHeight);
	} else {
		return null
	}

	useEffect(() => {
		setData(title)
		const svg = select(svgRef.current);
		svg.selectAll("circle")
		.data(data)
		.join("circle")	
		// .attr("r", value => value)
		// .attr("cx", value => value *  2)
		// .attr("cy", value => value * 2)
  }, [data, windowsWidth, windowsHeight])

return (
	<React.Fragment>
		<svg ref={svgRef}  width={windowsWidth} height={windowsHeight}></svg>
		<br/>
		<button onClick={() => setData(data.map(value => value + 5))}>
		Update
		</button>
	</React.Fragment>
 )

}

export default Page
