import React, {useEffect, useState, useRef} from 'react'
import dynamic from 'next/dynamic'
import { select, hierarchy, tree, linkVertical, forceSimulation } from "d3"
import json from './lang'

const Page = (props) => {
	const svgRef = useRef()
	useEffect(() => {
		const svg = select(svgRef.current)
		const root = hierarchy(json)
    const nodeData = root.descendants()
    const linkData = root.links()

    const simulation = forceSimulation(nodeData).on("tick", () => {
      console.log("current force", simulation.alpha())
    })
	}, [])

return (
	<React.Fragment>
		<svg ref={svgRef}  width='2000px' height='2000px'></svg>
		<br/>
    <h1>Forced Layout</h1>
	</React.Fragment>
 )

}

export default Page
