import React, {useEffect, useState, useRef} from 'react'
import dynamic from 'next/dynamic'
import { select } from "d3"

const Page = (props) => {
	// let [data, setData] = useState();
	const svgRef = useRef();
	let title = null

	useEffect(() => {
		const svg = select(svgRef.current);
		const data = props.props.map(e => {
			const title = []
			const traffic = []
			title.push(e.title)
			traffic.push(parseInt(e.traffic.match(/\d+/)[0]))
			return title
		});
		// setData(title)
		console.log(data)
		svg.selectAll("circle")
		.data(data.title)
		.join("circle")	
		// .attr("r", data.traffic)
		// .attr("cx", value => value *  2)
		// .attr("cy", value => value * 2)
	}, [])

return (
	<React.Fragment>
		<svg ref={svgRef}  width='2000px' height='2000px'></svg>
		<br/>
		<button onClick={() => setData(data.map(value => value + 5))}>
		Update
		</button>
	</React.Fragment>
 )

}

export default Page
