import React, {useEffect, useState, useRef} from 'react'
import dynamic from 'next/dynamic'
import { select, hierarchy, tree, linkVertical, forceSimulation, forceCenter, mouse,  forceX,  forceY, forceCollide, forceRadial} from "d3"
import json from './lang'
import { forceManyBody } from 'd3-force';
import useResizeObserver from '../hooks/useResizeObeserver'

// const useResizeObserver = ref => {
//   const [dimensions, setDimensions] = useState()

//   useEffect(() => {
//   const observeTarget = ref.current
//   const resizeObserever = new useResizeObserver((entries) => {
//     console.log(entries)
//   })
//   resizeObserver.observe(observeTarget)
//   return () => {
//     resizeObserever.unobserve(observeTarget)
//   }
//   }, [ref])
//   return dimensions;

// }

const Page = (props) => {
  const svgRef = useRef()
  const wrapperRef = useRef()
  const dimensions = useResizeObserver(wrapperRef)
	useEffect(() => {
		const svg = select(svgRef.current)
		const root = hierarchy(json)
    const nodeData = root.descendants()
    const linkData = root.links()

    const simulation = forceSimulation(nodeData)
    // force
    .force("center", forceCenter(1000, 1000))
    .force("charge", forceManyBody().strength(-40))
    .force("collide", forceCollide(60))
    .on("tick", () => {
      const linkGenerator = linkVertical()
      .x(node => node.x)
      .y(node => node.y + 100)

      svg
      .selectAll(".node")
      .data(nodeData)
      .join("circle")
      .attr("class", "node")
      .attr("r", 5)
      .attr("fill", "grey")
      .attr("cx", node => node.x)
      .attr("cy", node => node.y + 100)

      svg
      .selectAll(".link")
      .data(linkData)
      .join("path").attr("class","link")
      .attr("fill", "none")
      .attr("stroke", "grey")
      .attr("d", linkGenerator)
      .attr("x1", link => link.source.x)
      .attr("y1", link => link.source.y)
      .attr("x2", link => link.target.x)
      .attr("y2", link => link.target.y)

      svg
      .selectAll(".label")
      .data(root.descendants())
      .join("text")
      .attr("class", "label")
      .attr("fill", "black")
      .text(node => node.data.language)
      .attr("font-size", 16)
      .attr("text-anchor", "middle")
      .attr("x", node => node.x)
      .attr("y", node => node.y + 85)

    })

    svg.on("mousemove", ()=> {
      const [x,y] = mouse(svgRef.current)
      simulation
      .force("x", forceX(x).strength(node => 0.2 + node.depth * 0.15))
      .force("y", forceY(y).strength(node => 0.2 + node.depth * 0.15))
    })

    svg.on("click", () => {
       const [x, y] = mouse(svgRef.current)
       simulation
       .alpha(0.5)
       .restart()
       .force("orbit", forceRadial(100, x, y))

       svg.selectAll(".orbit")
       .data([json])
       .join("circle")
       .attr("class", "orbit")
       .attr("fill", "none")
       .attr("r", 500)
       .attr("cx", x)
       .attr("cy", y)
    })
	}, [json, dimensions])

return (
	<React.Fragment>
    <div ref={wrapperRef}>
      	<svg ref={svgRef}></svg>
    </div>
		<br/>
    <h1>Forced Layout</h1>
	</React.Fragment>
 )

}

export default Page
