import {useEffect, useState, useRef} from 'react'
import dynamic from 'next/dynamic'
import {select} from "d3"

const Page = (props) => {
  const [data, setData] = useState([30, 15, 20, 50]);
  const svgRef = useRef();
  useEffect(() => {
  console.log(svgRef)
  const svg = select(svgRef.current);
  svg
  .selectAll("circle")
  .data(data)
  .join(
    enter => enter.append("circle")
	.attr("class", "new").attr("r", value => value)
	.attr("cx", value *  2)
	.attr("cy", value * 2)
	.attr("stroke", "red"),
    update => update.attr("class", "updated"),
    exit => exit.remove()
  );
  }, [data])

return (
  <React.Fragment>
   <svg ref={svgRef}></svg>
   <br/>
   <button onClick={()=> setData(data.map(value => value + 5))}>
   Update
   </button>
  </React.Fragment>
 )

}

export default Page
