import React, {useEffect, useState, useRef} from 'react'
import dynamic from 'next/dynamic'
import { select } from "d3"

const Page = (props) => {
	// const [windowsWidth, setWidth] = useState()
	// const [windowsHeight, setHeight] = useState()
	const [data, setData] = useState(null);
	const svgRef = useRef();
	const title = props.props.map(e => e.title);
	// const width = typeof window === 'object' ? window.innerWidth : null
	// const height = typeof window === 'object' ? window.innerHeight : null

	const size = useWindowSize();
	function useWindowSize() {
		const isClient = typeof window === 'object';

		function getSize() {
			return {
				width: isClient ? window.innerWidth : 0,
				height: isClient ? window.innerHeight : 0
			};
		}
	}

	useEffect(() => {
		if (!isClient) {
      return false;
    }

    function handleResize() {
      setWindowSize(getSize());
    }

		setData(title)
		const svg = select(svgRef.current);
		svg.selectAll("circle")
		.data(data)
		.join("circle")	
		// .attr("r", value => value)
		// .attr("cx", value => value *  2)
		// .attr("cy", value => value * 2)

		window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);

	}, [data])

return (
	<React.Fragment>
		<svg ref={svgRef}  width={size.width} height={size.height}></svg>
		<br/>
		<button onClick={() => setData(data.map(value => value + 5))}>
		Update
		</button>
	</React.Fragment>
 )

}

export default Page
